var import_compiler = require("@marko/compiler");
module.exports = (path) => {
  const {
    node,
    hub: { file }
  } = path;
  path.get("body").unshiftContainer("body", import_compiler.types.variableDeclaration("var", [
    import_compiler.types.variableDeclarator(file._componentDefIdentifier, node.body.params[0]),
    import_compiler.types.variableDeclarator(file._componentInstanceIdentifier, node.body.params[1])
  ]));
};
